	function get_color($color, $per){
		$color = str_replace("#","",$color);
		if($per==0)return $color;
		$rgb = '';
	    $per = $per/100*255;
	    if  ($per < 0 ){
	        $per =  abs($per);
	        for ($x=0;$x<3;$x++){
	            $c = hexdec(substr($color,(2*$x),2)) - $per;
	            $c = ($c < 0) ? 0 : dechex($c);
	            $rgb .= (strlen($c) < 2) ? '0'.$c : $c;
	        }  
	    }else{
	        for ($x=0;$x<3;$x++){            
	            $c = hexdec(substr($color,(2*$x),2)) + $per;
	            $c = ($c > 255) ? 'ff' : dechex($c);
	            $rgb .= (strlen($c) < 2) ? '0'.$c : $c;
	        }   
	    }
	    return '#'.$rgb;
	} 

